    public static List<Integer> findSubstring(String s, String[] words) {
        List<Integer> ret = new LinkedList<>();
        if(words.length<1 || words.length>30){
            //return ret;
        }
        List<String> wordSet = new LinkedList<>();
        for( String word : words ){
            wordSet.add(word);
        }
        int idx = 0;
        while ( idx < s.length() ){
            List<String> wordSetCopy = new LinkedList<>(wordSet);
            boolean found = false;
            int currentIdx = idx;
            for(int i = 0; i < words.length; i ++){
                found = false;
                Iterator<String> it = wordSetCopy.iterator();
                while (it.hasNext()) {
                    String tWord = it.next();
                    if( doesSubstringStartsWith(s, currentIdx, tWord)){
                        it.remove();
                        found = true;
                        currentIdx += tWord.length();
                        break;
                    }
                }
                if(!found){
                    break;
                }
            }
            if(found) {
                ret.add(idx);
            }
            idx ++;
        }
        return ret;
    }

    private static boolean doesSubstringStartsWith(String s, int idx, String word) {
        int charsFound = 0;
        for(int i = 0; i < word.length() && idx + i < s.length(); i ++ ){
            if(s.charAt(idx + i) != word.charAt(i)){
                return false;
            }
            charsFound ++;
        }
        return charsFound == word.length();
    }
